import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import LogoImg from './images/mylogo.svg';

const Navbar = () => {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="navbar">
      <div className="logo">
        <Link id="logo" to="/">
          <img id="logoimg" src={LogoImg} alt="RestaurantLogo" />
        </Link>
      </div>

      <div className="links">
        <Link to="/">Home</Link>
        <div className="menu-dropdown">
          <Link to="/menu" onMouseEnter={toggleMenu} onMouseLeave={toggleMenu}>
            Menu
          </Link>
          {isMenuOpen && (
            <div className="dropdown-content">
              <Link to="/menu/starters">Starters</Link>
              <Link to="/menu/platters">Platters</Link>
              <Link to="/menu/mains">Mains</Link>
              <Link to="/menu/drinks">Drinks</Link>
            </div>
          )}
        </div>
        <Link to="/reviewspage">Reviews</Link>
        <Link id="homeorderonline" to="/orderonlinepage">
          Order Online
        </Link>
        <Link id="homeorderonline" to="/book">
          Book Now
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
