import React, { useState } from 'react';

const ReviewsPage = () => {
  const [reviews, setReviews] = useState([
    { id: 1, text: 'DODO Restaurant is the best!', likes: 10 },
    { id: 2, text: 'The owner is always there to welcome you! Would recommend.', likes: 5 },
    { id: 3, text: 'Awesome ambiance!', likes: 8 },
  ]);
  const [newReviewText, setNewReviewText] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const handleLikeReview = (reviewId) => {
    const updatedReviews = reviews.map((review) => {
      if (review.id === reviewId) {
        return { ...review, likes: review.likes + 1 };
      }
      return review;
    });
    setReviews(updatedReviews);
  };

  const handleAddReview = () => {
    const newReview = {
      id: reviews.length + 1,
      text: newReviewText,
      likes: 0,
    };
    setReviews([...reviews, newReview]);
    setNewReviewText('');
  };

  // Filter reviews based on the search term
  const filteredReviews = reviews.filter((review) =>
    review.text.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort filtered reviews based on the number of likes in descending order
  const sortedReviews = filteredReviews.sort((a, b) => b.likes - a.likes);

  return (
    <div className="reviews-page">
      <h1 className="reviews-title">Customer Reviews</h1>
      <div className="search-bar">
        <input
        id='menusearch'
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a review..."
        />
      </div>
      <div className="reviews-list">
        {sortedReviews.map((review) => (
          <div key={review.id} className="review-item">
            <p>{review.text}</p>
            <div className="review-likes">
              <span>Likes: {review.likes}</span>
              <button onClick={() => handleLikeReview(review.id)}>Like</button>
            </div>
          </div>
        ))}
      </div>
      <div className="add-review">
        <h2>Write a Review</h2>
        <textarea
          value={newReviewText}
          onChange={(e) => setNewReviewText(e.target.value)}
          placeholder="Write your review..."
        ></textarea>
        <button onClick={handleAddReview}>Submit</button>
      </div>
    </div>
  );
};

export default ReviewsPage;
