import React from 'react';

const MainsStep = ({ onNextStep, onPreviousStep, onSelectedItemsChange, selectedItems }) => {
  const mains = [
    { id: 13, name: 'Mine frite (🌶️)', price: 10 },
    { id: 14, name: 'Riz frite (🌶️)', price: 8 },
    { id: 15, name: 'Haalim with bread (🌶️🌶️🌶️|🍃)', price: 10 },
    { id: 17, name: 'Dhall puri with curry (🌶️|🍃)', price: 8 },
    { id: 18, name: 'Vermicelli noodles (🌶️)', price: 10 },
    { id: 19, name: 'Pain kebab (🌶️🌶️)', price: 8 },
  ];

  const handleItemChange = (itemId, quantity) => {
    const updatedItems = selectedItems.map((item) => {
      if (item.id === itemId) {
        return { ...item, quantity };
      }
      return item;
    });
    onSelectedItemsChange(updatedItems);
  };

  const handleNextClick = () => {
    onNextStep();
  };

  const handlePreviousClick = () => {
    onPreviousStep();
  };

  return (
    <div className="step">
      <h2>Step 3: Mains</h2>
      <div className="items-list">
        {mains.map((item) => (
          <div key={item.id} className="item">
            <label htmlFor={`quantity-${item.id}`}>
              {item.name} - ${item.price}
            </label>
            <input
              type="number"
              id={`quantity-${item.id}`}
              value={selectedItems.find((selected) => selected.id === item.id)?.quantity || 0}
              min={0}
              onChange={(e) => handleItemChange(item.id, parseInt(e.target.value))}
            />
          </div>
        ))}
      </div>
      <button onClick={handlePreviousClick}>Previous</button>
      <button onClick={handleNextClick}>Next</button>
    </div>
  );
};

export default MainsStep;
