import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import LogoImg from './images/mylogo.svg';

const Navbar = () => {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [activeLanguage, setActiveLanguage] = useState('EN');

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  const handleLanguageClick = (language) => {
    setActiveLanguage(language);
  };

  return (
    <nav className="navbar">
      <div className="logo">
        <Link id="logo" to="/">
          <img id="logoimg" src={LogoImg} alt="RestaurantLogo" />
        </Link>
      </div>

      <div className="links">
        {/* Rest of the menu links */}
        <div id="language-container">
          <div
            className={`language-switch ${
              activeLanguage === 'EN' ? 'active-language' : ''
            }`}
            onClick={() => handleLanguageClick('EN')}
          >
            <div className={`language-button EN`}>EN</div>
            <div className="language-label">English</div>
          </div>
          <div
            className={`language-switch ${
              activeLanguage === 'FR' ? 'active-language' : ''
            }`}
            onClick={() => handleLanguageClick('FR')}
          >
            <div className={`language-button FR`}>FR</div>
            <div className="language-label">French</div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
