import React from 'react';

const StartersStep = ({ onNextStep, onSelectedItemsChange, selectedItems }) => {
  const starters = [
    { id: 1, name: 'Fried Calamari (🌶️)', price: 10 },
    { id: 2, name: 'Cheesy Bites (🌶️)', price: 5 },
    { id: 3, name: 'Tuna and crab cakes (🌶️🌶️)', price: 10 },
    { id: 4, name: 'Gâteau piment (🌶️🌶️|🍃)', price: 5 },
    { id: 5, name: 'Fish ball and seafood soup (🌶️🌶️🌶️)', price: 10 },
    { id: 6, name: 'vegetable dumplings soup (🌶️|🍃)', price: 5 },
  ];

  const handleItemChange = (itemId, quantity) => {
    const updatedItems = selectedItems.map((item) => {
      if (item.id === itemId) {
        return { ...item, quantity };
      }
      return item;
    });
    onSelectedItemsChange(updatedItems);
  };

  const handleNextClick = () => {
    onNextStep();
  };

  return (
    <div className="step">
      <h2>Step 1: Starters</h2>
      <div className="items-list">
        {starters.map((item) => (
          <div key={item.id} className="item">
            <label htmlFor={`quantity-${item.id}`}>
              {item.name} - ${item.price}
            </label>
            <input
              type="number"
              id={`quantity-${item.id}`}
              value={selectedItems.find((selected) => selected.id === item.id)?.quantity || 0}
              min={0}
              onChange={(e) => handleItemChange(item.id, parseInt(e.target.value))}
            />
          </div>
        ))}
      </div>
      <button onClick={handleNextClick}>Next</button>
    </div>
  );
};

export default StartersStep;
