import React from 'react';

const PlattersStep = ({ platters, handleQuantityChange, items }) => {
  const calculateTotalPrice = () => {
    const plattersItems = items.filter(item => item.category === 'Platters');
    return plattersItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <div>
      <h2>Platters</h2>
      <div className="total-price">Total Price: ${calculateTotalPrice()}</div>
      {platters.map(item => (
        <div key={item.id} className="order-item">
          <span>{item.name}</span>
          <span>${item.price}</span>
          <input
            type="number"
            value={item.quantity}
            min={0}
            onChange={e => handleQuantityChange(item.id, parseInt(e.target.value))}
          />
        </div>
      ))}
    </div>
  );
};

export default PlattersStep;
