import React from 'react';

const PlattersStep = ({ onNextStep, onPreviousStep, onSelectedItemsChange, selectedItems }) => {
  const platters = [
    { id: 7, name: 'Farata and choice of 2 curries (🌶️|🍃)', price: 15 },
    { id: 8, name: 'La daube poulet (🌶️🌶️)', price: 15 },
    { id: 9, name: 'Fish masala curry (🌶️🌶️🌶️)', price: 15 },
    { id: 10, name: 'Octopus stew (🌶️)', price: 15 },
    { id: 11, name: 'Shrimp curry (🌶️🌶️)', price: 15 },
    { id: 12, name: '7 curries platter (🌶️|🍃)', price: 15 },
  ];

  const handleItemChange = (itemId, quantity) => {
    const updatedItems = selectedItems.map((item) => {
      if (item.id === itemId) {
        return { ...item, quantity };
      }
      return item;
    });
    onSelectedItemsChange(updatedItems);
  };

  const handleNextClick = () => {
    onNextStep();
  };

  const handlePreviousClick = () => {
    onPreviousStep();
  };

  return (
    <div className="step">
      <h2>Step 2: Platters</h2>
      <div className="items-list">
        {platters.map((item) => (
          <div key={item.id} className="item">
            <label htmlFor={`quantity-${item.id}`}>
              {item.name} - ${item.price}
            </label>
            <input
              type="number"
              id={`quantity-${item.id}`}
              value={selectedItems.find((selected) => selected.id === item.id)?.quantity || 0}
              min={0}
              onChange={(e) => handleItemChange(item.id, parseInt(e.target.value))}
            />
          </div>
        ))}
      </div>
      <button onClick={handlePreviousClick}>Previous</button>
      <button onClick={handleNextClick}>Next</button>
    </div>
  );
};

export default PlattersStep;
