import React, { useState } from 'react';
import { Link } from 'react-router-dom';
// import './NewsletterPage.css';

const NewsletterPage = () => {
    const [subscribed, setSubscribed] = useState(false);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [dailyOffers, setDailyOffers] = useState(false);
    const [futureEvents, setFutureEvents] = useState(false);

    const handleSubscribe = () => {
        // Your subscribe logic can be added here, such as sending the data to a server
        // For this example, we will simply set the subscribed state to true
        setSubscribed(true);
    };

    return (
        <div className="newsletter-page">
            <h1>Subscribe to Our Newsletter</h1>
            {subscribed ? (
                <div className="confirmation">

                    <div className="confirmation-text">
                    <h1>-</h1>
                        <p>Thanks {name}!</p>
                        <p>You are now subscribed to our newsletter.</p>
                        <Link to="/">Go back home</Link>
                    </div>
                </div>
            ) : (
                <div className="subscription-box">
                    <input
                        type="text"
                        placeholder="Your Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <input
                        type="email"
                        placeholder="Your Email Address"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="tel"
                        placeholder="Your Phone Number"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                    />
                    <div className="checkboxes">
                        <label>
                            <input
                                type="checkbox"
                                checked={dailyOffers}
                                onChange={() => setDailyOffers(!dailyOffers)}
                            />
                            Daily Offers
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={futureEvents}
                                onChange={() => setFutureEvents(!futureEvents)}
                            />
                            Future Events
                        </label>
                    </div>
                    <button className="subscribe-button" onClick={handleSubscribe}>
                        Subscribe
                    </button>
                </div>
            )}
        </div>
    );
};

export default NewsletterPage;
