        { id: 2, name: 'Cheesy Bites (ðŸŒ¶ï¸)', category: 'Starters', price: 5, quantity: 0 },
        { id: 3, name: 'Tuna and crab cakes (ðŸŒ¶ï¸ðŸŒ¶ï¸)', category: 'Starters', price: 10, quantity: 0 },
