import React, { useState } from 'react';
import StartersStep from './StartersStep';
import PlattersStep from './PlattersStep';
import MainsStep from './MainsStep';
import DrinksStep from './DrinksStep';
import CheckoutStep from './CheckoutStep';

const OrderOnlinePage = () => {
  const [items, setItems] = useState([
    // items data
  ]);

  const handleQuantityChange = (itemId, value) => {
    const updatedItems = items.map(item => {
      if (item.id === itemId) {
        return { ...item, quantity: value };
      }
      return item;
    });
    setItems(updatedItems);
  };

  const calculateTotalPrice = () => {
    return items.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleConfirmAndPay = () => {
    // to be implemented later
  };

  const starters = items.filter(item => item.category === 'Starters');
  const platters = items.filter(item => item.category === 'Platters');
  const mains = items.filter(item => item.category === 'Mains');
  const drinks = items.filter(item => item.category === 'Drinks');

  return (
    <div className="order-online-page">
      <h1 className="order-online-title">Order Online</h1>
      <div className="order-online-box">
        {/* Page 1: Starters Step */}
        <StartersStep starters={starters} handleQuantityChange={handleQuantityChange} />

        {/* Page 2: Platters Step */}
        <PlattersStep platters={platters} handleQuantityChange={handleQuantityChange} />

        {/* Page 3: Mains Step */}
        <MainsStep mains={mains} handleQuantityChange={handleQuantityChange} />

        {/* Page 4: Drinks Step */}
        <DrinksStep drinks={drinks} handleQuantityChange={handleQuantityChange} />

        {/* Page 5: Checkout Step */}
        <CheckoutStep totalPrice={calculateTotalPrice()} handleConfirmAndPay={handleConfirmAndPay} />
      </div>
    </div>
  );
};

export default OrderOnlinePage;
