import React, { useState } from 'react';
import StartersStep from './StartersStep';
import PlattersStep from './PlattersStep';
import MainsStep from './MainsStep';
import DrinksStep from './DrinksStep';
import CheckoutStep from './CheckoutStep';

const OrderOnlinePage = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedItems, setSelectedItems] = useState([]);

  const handleNextStep = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const handlePreviousStep = () => {
    setCurrentStep((prevStep) => prevStep - 1);
  };

  const handleSelectedItemsChange = (itemId, quantity) => {
    const updatedItems = selectedItems.map((item) => {
      if (item.id === itemId) {
        return { ...item, quantity };
      }
      return item;
    });
    setSelectedItems(updatedItems);
  };

  const calculateTotalPrice = () => {
    return selectedItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <StartersStep
            onNextStep={handleNextStep}
            onSelectedItemsChange={handleSelectedItemsChange}
            selectedItems={selectedItems}
          />
        );
      case 2:
        return (
          <PlattersStep
            onNextStep={handleNextStep}
            onPreviousStep={handlePreviousStep}
            onSelectedItemsChange={handleSelectedItemsChange}
            selectedItems={selectedItems}
          />
        );
      case 3:
        return (
          <MainsStep
            onNextStep={handleNextStep}
            onPreviousStep={handlePreviousStep}
            onSelectedItemsChange={handleSelectedItemsChange}
            selectedItems={selectedItems}
          />
        );
      case 4:
        return (
          <DrinksStep
            onNextStep={handleNextStep}
            onPreviousStep={handlePreviousStep}
            onSelectedItemsChange={handleSelectedItemsChange}
            selectedItems={selectedItems}
          />
        );
      case 5:
        return (
          <CheckoutStep
            onPreviousStep={handlePreviousStep}
            selectedItems={selectedItems}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="order-online-page">
      <h1 className="order-online-title">Order Online</h1>
      <div className="order-online-box">
        <div className="cost-display">Total Price: ${calculateTotalPrice()}</div>
        {renderStep()}
      </div>
    </div>
  );
};

export default OrderOnlinePage;
