import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

const OrderConfirmation = () => {
  const [countdown, setCountdown] = useState(1800); // 30 minutes in seconds
  const history = useHistory();

  useEffect(() => {
    const timer = setInterval(() => {
      setCountdown(prevCountdown => prevCountdown - 1);
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  const formatTime = (countdown) => {
    const minutes = Math.floor(countdown / 60);
    const seconds = countdown % 60;
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  const handleGoToLocation = () => {
    history.push('/location');
  };

  const handleGoHome = () => {
    history.push('/');
  };

  return (
    <div className="order-confirmation">
      <div className="confirmation-message">
        <p>Thank you for ordering online at DODO Restaurant.</p>
        <p>Your order should be ready in {formatTime(countdown)} minutes</p>
        <p>We are looking forward to meeting you.</p>
      </div>

      <div className="buttons">
        <button className="our-location" onClick={handleGoToLocation}>Our Location</button>
        <button className="go-home" onClick={handleGoHome}>Go back home</button>
      </div>
    </div>
  );
}

export default OrderConfirmation;
