import React, { useState } from 'react';
// import './ReviewsPage.css';

const ReviewsPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredReviews, setFilteredReviews] = useState(null);

  // Your review data should be an array of objects with 'name' and 'review' properties
  const reviewsData = [
    { name: 'John Doe', review: 'Great service and delicious food!' },
    { name: 'Jane Smith', review: 'The ambiance was perfect for a family dinner.' },
    // Add more reviews here
  ];

  const handleSearch = () => {
    const filtered = reviewsData.filter(
      (review) =>
        review.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        review.review.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredReviews(filtered);
  };

  return (
    <div className="reviews-page">
      <h1>Customer Reviews</h1>
      <div className="search-box">
        <input
          type="text"
          placeholder="Search for a review..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {filteredReviews ? (
        <div className="reviews-container">
          {filteredReviews.map((review, index) => (
            <div key={index} className="review">
              <p className="review-name">{review.name}</p>
              <p className="review-text">{review.review}</p>
            </div>
          ))}
        </div>
      ) : (
        <div className="reviews-container">
          {reviewsData.map((review, index) => (
            <div key={index} className="review">
              <p className="review-name">{review.name}</p>
              <p className="review-text">{review.review}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ReviewsPage;
