import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const OrderConfirmation = () => {
    const [countdown, setCountdown] = useState(1800); // 30 minutes in seconds
    const [orderNumber, setOrderNumber] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const timer = setInterval(() => {
            setCountdown(prevCountdown => prevCountdown - 1);
        }, 1000);

        // Generate a random order number
        const randomOrderNumber = Math.floor(Math.random() * 1000000);
        setOrderNumber(randomOrderNumber.toString());

        return () => {
            clearInterval(timer);
        };
    }, []);

    const formatTime = countdown => {
        const minutes = Math.floor(countdown / 60);
        const seconds = countdown % 60;
        return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    };

    const handleGoToLocation = () => {
        navigate('/location');
    };

    const handleGoHome = () => {
        navigate('/');
    };

    return (
        <div className="online-confirmation">
            <div id='onlineconfirmationmessage' className="confirmation-message">
                <p id='onlinethankyou' >Thank you for ordering online at DODO Restaurant.</p>
                <p>Your order should be ready in {formatTime(countdown)} minutes</p>
                <p>Your order number is {orderNumber}</p>
                <p id='onlinethankyou' >We are looking forward to meeting you.</p>
            </div>

            <div className="buttons">
                <button className="our-location" onClick={handleGoToLocation}>Our Location</button>
                <button className="go-home" onClick={handleGoHome}>Go back home</button>
            </div>
        </div>
    );
};

export default OrderConfirmation;
