import React, { useState } from 'react';

const Menu = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const menuItems = [
    {
      category: 'Starters',
      items: [
        'Fried Calamari (üå∂Ô∏è) - $10',
        'Cheesy Bites (üå∂Ô∏è) - $5',
        'Tuna and crab cakes (üå∂Ô∏èüå∂Ô∏è) - $10',
        'G√¢teau piment (üå∂Ô∏èüå∂Ô∏è|üçÉ)  - $5',
        'Fish ball and seafood soup (üå∂Ô∏èüå∂Ô∏èüå∂Ô∏è) - $10',
        'Vegetable dumplings soup (üå∂Ô∏è|üçÉ) - $5',
      ],
    },
    {
      category: 'Platters',
      items: [
        'Farata and choice of 2 curries (üå∂Ô∏è|üçÉ) - $15',
        'La daube poulet (üå∂Ô∏èüå∂Ô∏è) - $15',
        'Fish masala curry (üå∂Ô∏èüå∂Ô∏èüå∂Ô∏è) - $15',
        'Octopus stew (üå∂Ô∏è) - $15',
        'Shrimp curry (üå∂Ô∏èüå∂Ô∏è) - $15',
        '7 curries platter (üå∂Ô∏è|üçÉ) - $15',
      ],
    },
    {
      category: 'Mains',
      items: [
        'Mine frite (üå∂Ô∏è) - $10',
        'Riz frite (üå∂Ô∏è) - $8',
        'Haalim with bread (üå∂Ô∏èüå∂Ô∏èüå∂Ô∏è|üçÉ) - $10',
        'Dhall puri with curry (üå∂Ô∏è|üçÉ) - $8',
        'Vermicelli noodles (üå∂Ô∏è) - $10',
        'Pain kebab (üå∂Ô∏èüå∂Ô∏è) - $8',
      ],
    },
    {
      category: 'Drinks',
      items: [
        'Panakon - $3',
        'Lemonade - $4',
        'Coconut water - $3',
        'Eski - $4',
        'Pearona - $3',
        'Fanta Passion - $4',
      ],
    },
  ];

  const filteredItems = menuItems
    .filter((category) =>
      category.items.some((item) =>
        item.toLowerCase().includes(searchQuery.toLowerCase())
      )
    )
    .map((category) => ({
      ...category,
      items: category.items.filter((item) =>
        item.toLowerCase().includes(searchQuery.toLowerCase())
      ),
    }));

  return (
    <div className="menu-page">
      <h1 className="menu-title">Our Menu</h1>
      <h3>
        Mild - üå∂Ô∏è | Hot - üå∂Ô∏èüå∂Ô∏è | Very Hot - üå∂Ô∏èüå∂Ô∏èüå∂Ô∏è | Vegetarian - üçÉ
      </h3>
      <div className="menu-box">
        <div className="menu-search">
          <input
            type="text"
            value={searchQuery}
            onChange={handleSearch}
            placeholder="Search food item..."
          />
        </div>

        {filteredItems.map((category, index) => (
          <div key={index} className="menu-section">
            <h2>{category.category}</h2>
            <ul>
              {category.items.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>
        ))}

        <div className="menu-guide">
          <div className="guide"></div>
        </div>
      </div>
    </div>
  );
};

export default Menu;
