import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FrenchFooter from './FrenchFooter';
import LogoImg from './images/mylogo.svg';
const FrenchMenu = () => {
    const [isMenuOpen, setMenuOpen] = useState(false);
  const [activeLanguage, setActiveLanguage] = useState('FR');

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  const handleLanguageClick = (language) => {
    setActiveLanguage(language);
  };
    const [searchQuery, setSearchQuery] = useState('');

    const handleSearch = (e) => {
        setSearchQuery(e.target.value);
    };

    const menuItems = [
        {
            category: 'Starters',
            items: [
                'Fried Calamari (🌶️) - $10',
                'Cheesy Bites (🌶️) - $5',
                'Tuna and crab cakes (🌶️🌶️) - $10',
                'Gâteau piment (🌶️🌶️|🍃)  - $5',
                'Fish ball and seafood soup (🌶️🌶️🌶️) - $10',
                'Vegetable dumplings soup (🌶️|🍃) - $5',
            ],
        },
        {
            category: 'Platters',
            items: [
                'Farata and choice of 2 curries (🌶️|🍃) - $15',
                'La daube poulet (🌶️🌶️) - $15',
                'Fish masala curry (🌶️🌶️🌶️) - $15',
                'Octopus stew (🌶️) - $15',
                'Shrimp curry (🌶️🌶️) - $15',
                '7 curries platter (🌶️|🍃) - $15',
            ],
        },
        {
            category: 'Mains',
            items: [
                'Mine frite (🌶️) - $10',
                'Riz frite (🌶️) - $8',
                'Haalim with bread (🌶️🌶️🌶️|🍃) - $10',
                'Dhall puri with curry (🌶️|🍃) - $8',
                'Vermicelli noodles (🌶️) - $10',
                'Pain kebab (🌶️🌶️) - $8',
            ],
        },
        {
            category: 'Drinks',
            items: [
                'Panakon - $3',
                'Lemonade - $4',
                'Coconut water - $3',
                'Eski - $4',
                'Pearona - $3',
                'Fanta Passion - $4',
            ],
        },
    ];

    const filteredItems = menuItems
        .filter((category) =>
            category.items.some((item) =>
                item.toLowerCase().includes(searchQuery.toLowerCase())
            )
        )
        .map((category) => ({
            ...category,
            items: category.items.filter((item) =>
                item.toLowerCase().includes(searchQuery.toLowerCase())
            ),
        }));

    return (
      <div className="menupage">
         <nav className="navbar">
         <div className="logo">
                    <Link id="logo" to="/frenchhomepage">
                        <img id="logoimg" src={LogoImg} alt="RestaurantLogo" />
                    </Link>
                </div>

                <div className="links">
                    <Link to="/frenchhomepage">Accueil</Link>
                    <div
                        className="menu-dropdown"
                        onMouseEnter={toggleMenu}
                        onMouseLeave={toggleMenu}
                    >
                        <Link to="/frenchmenu">Menu</Link>
                        {isMenuOpen && (
                            <div className="dropdown-content">
                                <Link to="/frenchstarters" onClick={closeMenu}>
                                    Starters
                                </Link>
                                <Link to="/frenchplatters" onClick={closeMenu}>
                                    Platters
                                </Link>
                                <Link to="/frenchmains" onClick={closeMenu}>
                                    Mains
                                </Link>
                                <Link to="/frenchdrinks" onClick={closeMenu}>
                                    Drinks
                                </Link>
                            </div>
                        )}
                    </div>
                    <Link to="/frenchreviewspage">Commentaires</Link>
                    <Link id="homeorderonline" to="/frenchorderonlinepage">
                        Commander en ligne
                    </Link>
                    <Link id="homeorderonline" to="/frenchbook">
                        Réserver une table
                    </Link>


        <div id="language-container">
          <Link
            id="languagestyle"
            to="/menu"
            className={activeLanguage === 'EN' ? 'active-language' : ''}
            onClick={() => handleLanguageClick('EN')}
          >
            EN 
          </Link>

          <Link
            id="languagestyle"
            to="/frenchmenu"
            className={activeLanguage === 'FR' ? 'active-language' : ''}
            onClick={() => handleLanguageClick('FR')}
          >
           FR
          </Link>
        </div>
      </div>
    </nav>
        <div className="menu-page">

            <h1 className="menu-title">FR Our Menu</h1>
            <h3>
                Mild - 🌶️ | Hot - 🌶️🌶️ | Very Hot - 🌶️🌶️🌶️ | Vegetarian - 🍃
            </h3>

                <div className="menu-search">
                    <input
                    id='menusearch'
                        type="text"
                        value={searchQuery}
                        onChange={handleSearch}
                        placeholder="Search food item..."
                    />
                </div>
                <div className="menu-box">
                {filteredItems.map((category, index) => (
                    <div key={index} className="menu-section">
                        <h2>{category.category}</h2>
                        <ul>
                            {category.items.map((item, index) => (
                                <li key={index}>{item}</li>
                            ))}
                        </ul>
                    </div>
                ))}

                <div className="menu-guide">
                    <div className="guide"></div>
                </div>
            </div>
        </div>
        <FrenchFooter/>
        </div>
    );
};

export default FrenchMenu;
