import React from 'react';

const StartersStep = ({ onNextStep, onSelectedItemsChange, selectedItems }) => {
  const starters = [
    { id: 1, name: 'Fried Calamari (ðŸŒ¶ï¸)', price: 10, quantity: 0 },
    { id: 2, name: 'Cheesy Bites (ðŸŒ¶ï¸)', price: 5, quantity: 0 },
    { id: 3, name: 'Tuna and crab cakes (ðŸŒ¶ï¸ðŸŒ¶ï¸)', price: 10, quantity: 0 },
    { id: 4, name: 'GÃ¢teau piment (ðŸŒ¶ï¸ðŸŒ¶ï¸|ðŸƒ)', price: 5, quantity: 0 },
    { id: 5, name: 'Fish ball and seafood soup (ðŸŒ¶ï¸ðŸŒ¶ï¸ðŸŒ¶ï¸)', price: 10, quantity: 0 },
    { id: 6, name: 'vegetable dumplings soup (ðŸŒ¶ï¸|ðŸƒ)', price: 5, quantity: 0 },
  ];

  const handleQuantityChange = (itemId, value) => {
    const updatedItems = selectedItems.map((item) => {
      if (item.id === itemId) {
        return { ...item, quantity: value };
      }
      return item;
    });
    onSelectedItemsChange(updatedItems);
  };

  const handleNextClick = () => {
    onNextStep();
  };

  return (
    <div className="step">
      <h2>Step 1: Starters</h2>
      <div className="items-list">
        {starters.map((item) => (
          <div key={item.id} className="item">
            <label htmlFor={`quantity-${item.id}`}>
              {item.name} - ${item.price}
            </label>
            <input
              type="number"
              id={`quantity-${item.id}`}
              value={item.quantity}
              min={0}
              onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
            />
          </div>
        ))}
      </div>
      <button onClick={handleNextClick}>Next</button>
    </div>
  );
};

export default StartersStep;
